(function (e) {
    "use strict";
    e(function () {
        if (0 === e("#disable_comment_images").length)return;
        e("#comment_image_toggle").on("click", function () {
            if (confirm("By doing this, you will toggle Comment Images for all posts on your blog. Are you sure you want to do this?")) {
                e(this).attr("disabled", "disabled");
                e("#comment_image_source").val("button");
                e("#publish").trigger("click")
            }
        })
    })
})(jQuery);